mapper.delete:
DELETE
FROM user_meeting
WHERE meeting_id = #{meetingId}
  AND user_id = #{userId}

mapper.select:
select * from (select meeting_id, user_id, category, subject, file_name, total_member, present_member, status, count(*) over() as totalDataSize, (select #{pageable.currentPage} from dual) as currentPage, (select #{pageable.pagePerSize} from dual) as pagePerSize, row_number() over (order by meeting_id desc, reg_dt desc) as rnum from meeting where meeting_id > 0
and subject like '%' || #{condition.keyword} || '%' or context like '%' || #{condition.keyword} || '%'
and subject like '%' || #{condition.keyword} || '%'
and context like '%' || #{condition.keyword} || '%'
) where rnum between ((#{pageable.currentPage} - 1) * #{pageable.pagePerSize}) + 1 and #{pageable.currentPage} * #{pageable.pagePerSize}

select user_type
from user_meeting
where user_id = #{userId}
  and meeting_id = #{meetingId}
