mapper.select:
select user_id as userId, email, user_name as userName, password
from users
where email = #{email}

select * from (select m.MEETING_ID, m.SUBJECT, m.STATUS, u.USER_NAME, um.USER_TYPE, m.REG_DT, count(*) over () as totalDataSize, (select #{pageable.currentPage} from dual) as currentPage, (select #{pageable.pagePerSize} from dual) as pagePerSize, row_number() over (order by m.reg_dt) as rnum from MEETING m join MARKET.USER_MEETING UM on m.MEETING_ID = UM.MEETING_ID join MARKET.USERS U on UM.USER_ID = U.USER_ID where um.USER_ID = #{userId}
and UM.USER_TYPE = #{userType}
) where rnum between ((#{pageable.currentPage} - 1) * #{pageable.pagePerSize}) + 1 and #{pageable.currentPage} * #{pageable.pagePerSize}
